import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import NavBar from "../components/NavBar";
import backImg from "../assets/img/repas-back.png";
import { Link } from "react-router-dom";
import axios from "axios";
const AjoutRepas = () => {
  // ""
  const handleSubmit = (e) => {
    // Prevent the default submit and page reload
    e.preventDefault();

    // Handle validations
    axios
      .post("http://localhost:5000/api/repas", { nom, ing })
      .then((response) => {
        console.log(response);
        // Handle response
      });
  };
  const [nom, setName] = useState();
  const [ing, setIngredientt] = useState();
  return (
    <>
      <NavBar />
      <div>
        <div>
          <section>
            <div className="preambule-content-profile">
              <div>
                <form method="post" onSubmit={handleSubmit}>
                  <div className="recipe-table">
                    <div className="preambule-content">
                      <div className="content-title">
                        <h2>Fiche Repas</h2>
                      </div>
                    </div>
                    <table>
                      <tr>
                        <td>Nom</td>
                        <td>
                          <input
                            type="text"
                            className="name-value"
                            name="name"
                            id="nom"
                            value={nom}
                            onChange={(e) => setName(e.target.value)}
                          />
                          {/* <h3>{getRepas.nom}</h3> */}
                        </td>
                      </tr>
                      <tr>
                        <td>Ingrédients</td>
                        <td>
                          <input
                            type="text"
                            className="name-value"
                            id="ing"
                            value={ing}
                            onChange={(e) => setIngredient(e.target.value)}
                          />
                        </td>
                      </tr>
                      <tr>
                        <td>Restaurant</td>
                        <td>
                          <input type="text" className="name-value" />
                        </td>
                      </tr>
                    </table>

                    <div className="button-delete">
                      <div>
                        <button type="submit"> Enregistrer </button>
                        {/* <span>Enregistrer</span> */}
                        {/* </Link> */}
                      </div>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </section>
        </div>

        <div className="img-back-bottom">
          <img src={backImg} alt="" />
        </div>
      </div>
    </>
  );
};

export default AjoutRepas;
