import { Link } from "react-router-dom";

const Repas = ({ nom, ingredient, image, id }) => {
  //const Repas = () => {
  const [getRepas, setgetRepas] = useState([]);
  let paramID = useParams();
  const fetchGetRepas = async () => {
    await axios
      .get(`http://localhost:5000/api/repas/${id}`)
      .then((response) => setgetRepas(response.data));
  };
  window.scrollTo(0, 0);
  useEffect(() => {
    fetchGetRepas();
  }, []);
  return (
    <div className="recipe">
      <h3>{nom}</h3>
      <div className="img-container">
        <img src={`${image}`} alt="Logo-Recipe" />
      </div>

      <div className="info-container">
        <div className="info-title">
          <span>Ingrédient : {`${ingredient}`}</span>
        </div>
        <div className="button-res">
          <div>
            <Link
              style={{ textDecoration: "none" }}
              to={`/ficheRepas/${getRepas}`}
              className="logo-paiement"
            >
              <span>Réserver</span>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Repas;
