import React, { useEffect, useState } from "react";
import Repas from "../components/Repas";
import NavBar from "../components/NavBar";
import { Link } from "react-router-dom";
import axios from "axios";
const PageAccueil = () => {
  window.scrollTo(0, 0);
  // const [repasListe, setrepasListe] = useState([]);
  // const fetchRepas = () => {
  //   axios
  //     .get("http://localhost:5000/api/repas")
  //     .then((response) => setrepasListe(response.data));
  //   // .then((response) => console.log(response.data));
  // };
  // useEffect(() => {
  //   fetchRepas();
  // }, []);
  let [query, setQuery] = useState("e");
  let [data, setData] = useState([]);
  let [sortMethod, setSortMethod] = useState("top");
  const fetchData = () => {
    axios.get("http://localhost:5000/api/repas").then((response) => {
      console.log(response.data);
      setData(response.data);
    });
  };

  useEffect(() => {
    fetchData();
  }, []);

  const searchSubmit = (e) => {
    e.preventDefault();
    fetchData();
  };

  const searchInput = (e) => {
    setQuery(e.target.value);
  };

  const topFlopClick = () => {
    if (sortMethod === "top") {
      setSortMethod("flop");
    } else {
      setSortMethod("top");
    }
  };

  return (
    <>
      <NavBar />
      <div>
        <div>
          <div className="title">
            <h2>Menu du jour</h2>
            <div>
              <span>
                Manzer.re est une entreprise fic0ve qui permet aux commerçants
                de me8re en ligne leur repas du jour.
              </span>
            </div>
          </div>
          <div>
            <form className="formSearch" onSubmit={searchSubmit}>
              <input
                type="text"
                placeholder="Entrez le titre du film"
                onInput={searchInput}
              />
              <input type="submit" value="Rechercher" />
              <button
                onClick={topFlopClick}
                title="Trier les films par ordre de popularité"
              >
                {sortMethod.toUpperCase()}
              </button>
            </form>
          </div>
        </div>
        <section>
          <div className="repas-container">
            {data &&
              data
                // .sort((a, b) => {
                //   if (sortMethod === "top") {
                //     return b.vote_average - a.vote_average;
                //   } else {
                //     return a.vote_average - b.vote_average;
                //   }
                // })
                .map((repas, index) => {
                  return (
                    <Repas
                      key={index}
                      nom={repas.nom}
                      ingredient={repas.ingredient}
                      image={repas.image}
                      id={repas.id}
                    />
                  );
                })}
          </div>
        </section>
      </div>
    </>
  );
};

export default PageAccueil;
