import React, { useEffect, useState } from "react";
import Repas from "../components/Repas";
import NavBar from "../components/NavBar";
import backImg from "../assets/img/repas-back.png";
import { Link } from "react-router-dom";
import axios from "axios";
const FicheRepas = () => {
  window.scrollTo(0, 0);

  return (
    <>
      <NavBar />
      <div>
        <div className="preambule-content-profile">
          <div className="content-title-profile">
            <h2>Fiche Repas</h2>
          </div>
          <div className="content-form">
            <form onSubmit={searchSubmit}>
              <input
                type="text"
                placeholder="Rechercher"
                onInput={searchInput}
              />
              {/* <input type="submit" value="Rechercher" />
              <button
                onClick={topFlopClick}
                title="Trier les films par ordre de popularitÃ©"
              >
                {sortMethod.toUpperCase()}
              </button> */}
            </form>
          </div>
        </div>
        <section>
          <div className="repas-container">
            {data &&
              data
                // .sort((a, b) => {
                //   if (sortMethod === "top") {
                //     return b.vote_average - a.vote_average;
                //   } else {
                //     return a.vote_average - b.vote_average;
                //   }
                // })
                .map((repas, index) => {
                  return (
                    <Repas
                      key={index}
                      nom={repas.nom}
                      ingredient={repas.ingredient}
                      image={repas.image}
                      id={repas.id}
                    />
                  );
                })}
          </div>
        </section>
      </div>
      <div className="img-back-bottom">
        <img src={backImg} alt="" />
      </div>
    </>
  );
};

export default FicheRepas;
