import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import NavBar from "../components/NavBar";
import backImg from "../assets/img/repas-back.png";
import { Link } from "react-router-dom";
import axios from "axios";
const FicheRepas = () => {
  window.scrollTo(0, 0);
  const [getRepas, setgetRepas] = useState([]);

  let paramID = useParams();

  let nom, ing, rest;

  const fetchGetRepas = async () => {
    await axios
      .get(`http://localhost:5000/api/repas/${paramID.id}`)
      .then((response) => {
        setgetRepas(response.data);
      });
  };

  useEffect(() => {
    fetchGetRepas();
  }, []);
  if (typeof paramID.id === "undefined") {
    nom = "";
    ing = "";
    rest = "";
  } else {
    nom = getRepas.nom;
    ing = getRepas.ingredient;
    rest = "Exemple";
  }
  // ----------------------
  // update step
  // ----------------------
  // const [nomUpdate, setName] = useState();
  // const [ingredient, setIngredient] = useState();

  const [post, setPost] = useState({
    nomUpdate: "",
    ingredient: "",
  });
  const handleInput = (e) => {
    setPost({ ...post, [e.target.name]: e.target.value });
  };
  const handleSubmit = async (e) => {
    // Prevent the default submit and page reload
    e.preventDefault();

    // Handle validations
    await axios
      .put(`http://localhost:5000/api/repas/${paramID.id}`, { post })
      .then((response) => {
        console.log(response.data);
        if (response.status === 200) {
          alert("Repas modifié avec succès");
        }
        // Handle response
      });
  };
  return (
    <>
      <NavBar />
      <div>
        <div>
          <section>
            <div className="img-back-top">
              <img src={backImg} alt="" />
            </div>
            <div className="preambule-content-profile">
              <div>
                <form onSubmit={handleSubmit}>
                  <div className="recipe-table">
                    <div className="preambule-content">
                      <div className="content-title">
                        <h2>Fiches Repas</h2>
                      </div>
                    </div>
                    <table>
                      <tr>
                        <td>Nom</td>
                        <td>
                          <input
                            type="text"
                            name="nomUpdate"
                            defaultValue={nom}
                            onChange={handleInput}
                            className="name-value"
                          />
                          {/* <h3>{getRepas.nom}</h3> */}
                        </td>
                      </tr>
                      <tr>
                        <td>Ingrédients</td>
                        <td>
                          <input
                            type="text"
                            name="ingredient"
                            defaultValue={ing}
                            onChange={handleInput}
                            className="name-value"
                          />
                        </td>
                      </tr>
                      <tr>
                        <td>Restaurant</td>
                        <td>
                          <input
                            type="text"
                            value={rest}
                            className="name-value"
                          />
                        </td>
                      </tr>
                    </table>
                    {/* <div className="button-valid">
                      <div>
                        <span>Enregistrer</span>
                        {/* </Link> 
                      </div>
                    </div> */}
                    <div className="button-delete">
                      <input type="submit" value="Enregistrer" />
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </section>
        </div>

        <div className="img-back-bottom">
          <img src={backImg} alt="" />
        </div>
      </div>
    </>
  );
};

export default FicheRepas;
