import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import NavBar from "../components/NavBar";
import backImg from "../assets/img/repas-back.png";
import { Link } from "react-router-dom";
import axios from "axios";
const AjoutRepas = () => {
  const state = {
    nom: "",
    ing: "",
  };
  state = {
    repas: { nom: "", ing: "" },
  };
  const setAuthorState = (event) => {
    let field = event.target.name;
    let value = event.target.value;
    this.state.repas[field] = value;
    return this.setState({ author: this.state.repas });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    const repas = {
      nom: this.state.nom,
      ing: this.state.ing,
    };
    axios.post("http://localhost:5000/api/repas", { repas }).then((res) => {
      console.log(res);
      console.log(res.data);
      window.location = "/repas"; //This line of code will redirect you once the submission is succeed
    });
  };
  const handleChange = (event) => {
    this.setState({ nom: event.target.value, ing: event.target.value });
  };
  return (
    <>
      <NavBar />
      <div>
        <div>
          <section>
            <div className="preambule-content-profile">
              <div>
                <form onSubmit={handleSubmit}>
                  <div className="recipe-table">
                    <div className="preambule-content">
                      <div className="content-title">
                        <h2>Fiche Repas</h2>
                      </div>
                    </div>
                    <table>
                      <tr>
                        <td>Nom</td>
                        <td>
                          <input
                            type="text"
                            className="name-value"
                            name="nom"
                          />
                          {/* <h3>{getRepas.nom}</h3> */}
                        </td>
                      </tr>
                      <tr>
                        <td>Ingrédients</td>
                        <td>
                          <input
                            type="text"
                            className="name-value"
                            name="ing"
                          />
                        </td>
                      </tr>
                      <tr>
                        <td>Restaurant</td>
                        <td>
                          <input type="text" className="name-value" />
                        </td>
                      </tr>
                    </table>

                    <div className="button-delete">
                      <div>
                        <button type="submit"> Enregistrer </button>
                        {/* <span>Enregistrer</span> */}
                        {/* </Link> */}
                      </div>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </section>
        </div>

        <div className="img-back-bottom">
          <img src={backImg} alt="" />
        </div>
      </div>
    </>
  );
};

export default AjoutRepas;
