import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import NavBar from "../components/NavBar";
import backImg from "../assets/img/repas-back.png";
import { Link } from "react-router-dom";
import axios from "axios";
const FicheRepas = () => {
  window.scrollTo(0, 0);
  const [getRepas, setgetRepas] = useState([]);
  let paramID = useParams();

  let nom, ing, rest;

  const fetchGetRepas = async () => {
    await axios
      .get(`http://localhost:5000/api/repas/${paramID.id}`)
      .then((response) => setgetRepas(response.data));
  };

  useEffect(() => {
    fetchGetRepas();
  }, []);

  // start here my put code
  // const [putRepas, setPutRepas] = useState({
  //   nom: "",
  //   ingredient: "",
  // });
  // const handleInput = (e) => {
  //   setPutRepas({ ...putRepas, [e.target.name]: e.target.value });
  // };
  // function handleSubmit(e) {
  //   e.preventDefault();
  //   axios
  //     .put(`http://localhost:5000/api/repas/${paramID.id}`, { putRepas })
  //     .then((res) => {
  //       console.log(res.data);
  //       alert("Repas mis à jour avec succès");
  //     })
  //     .catch((err) => console.log(err));
  // }

  // delete
  // function deleteRow(id, e) {
  //   axios
  //     .delete(`http://localhost:5000/api/repas/${paramID.id}`)
  //     .then((res) => {
  //       console.log(res);
  //       console.log(res.data);
  //       alert("Repas supprimé avec succès");
  //       const posts = this.getRepas.filter((item) => item.id !== id);
  //       this.setState({ posts });
  //     })
  //     .catch((err) => console.log(err));
  // }
  return (
    <>
      <NavBar />
      <div>
        <div>
          <section>
            <div className="img-back-top">
              <img src={backImg} alt="" />
            </div>
            <div className="preambule-content-profile">
              <div>
                <form
                // onSubmit={handleSubmit}
                >
                  <div className="recipe-table">
                    <div className="preambule-content">
                      <div className="content-title">
                        <h2>Mon Repas</h2>
                      </div>
                    </div>
                    <table>
                      <tr>
                        <td>Nom</td>
                        <td>
                          <input
                            type="text"
                            value={nom}
                            name="nom"
                            // onChange={handleInput}
                            className="name-value"
                          />
                          {/* <h3>{getRepas.nom}</h3> */}
                        </td>
                      </tr>
                      <tr>
                        <td>Ingrédients</td>
                        <td>
                          <input
                            type="text"
                            value={ing}
                            name="ingredient"
                            // onChange={handleInput}
                            className="name-value"
                          />
                        </td>
                      </tr>
                      <tr>
                        <td>Restaurant</td>
                        <td>
                          <input
                            type="text"
                            value={rest}
                            className="name-value"
                          />
                        </td>
                      </tr>
                    </table>
                    <div className="button-valid">
                      <div>
                        <span>Enregistrer</span>
                        {/* </Link> */}
                      </div>
                    </div>
                    <div
                      className="button-delete"
                      onClick={(e) => this.deleteRow(getRepas.id, e)}
                    >
                      <div>
                        <span>Suppprimer</span>
                        {/* </Link> */}
                      </div>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </section>
        </div>

        <div className="img-back-bottom">
          <img src={backImg} alt="" />
        </div>
      </div>
    </>
  );
};

export default FicheRepas;
