import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Record = (props) => (
  <tr>
    <td>{props.repas.nom}</td>
    <td>{props.repas.ingredient}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.repas._id}`}>
        Modifier
      </Link>{" "}
      |
      <button
        className="btn btn-link"
        onClick={() => {
          props.deleteRecord(props.repas._id);
        }}
      >
        Supprimer
      </button>
    </td>
  </tr>
);

export default function RecordList() {
  const [repas, setRecords] = useState([]);

  // This method fetches the repas from the database.
  useEffect(() => {
    async function getRecords() {
      const response = await fetch(`http://localhost:5000/api/repas`);

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const repas = await response.json();
      setRecords(repas);
    }

    getRecords();

    return;
  }, [repas.length]);

  // This method will delete a record
  async function deleteRecord(id) {
    await fetch(`http://localhost:5000/api/repas/${id}`, {
      method: "DELETE",
    });

    const newRepas = repas.filter((el) => el._id !== id);
    setRecords(newRepas);
  }

  // This method will map out the repas on the table
  function recordList() {
    return repas.map((record) => {
      return (
        <Record
          record={record}
          deleteRecord={() => deleteRecord(record._id)}
          key={record._id}
        />
      );
    });
  }

  // This following section will display the table with the repas of individuals.
  return (
    <div>
      <h3>Menu du jour</h3>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Ingr√©dient</th>
          </tr>
        </thead>
        <tbody>{recordList()}</tbody>
      </table>
    </div>
  );
}
