import React, { useEffect, useState } from "react";
import logo from "../assets/img/logo-manzer.png";
import { Link } from "react-router-dom";
import GoogleLogin from "react-google-login";
function NavBar() {
  const [loginData, setLoginData] = useState(
    localStorage.getItem("loginData")
      ? JSON.parse(localStorage.getItem("loginData"))
      : null
  );
  const handleLogout = () => {
    localStorage.removeItem("loginData");
    setLoginData(null);
  };

  const handleFailure = (result) => {
    // alert(result);
    console.log(result);
  };
  const handleLogin = async (googleData) => {
    // console.log(googleData);
    const res = await fetch("api/google-login", {
      method: "POST",
      body: JSON.stringify({
        token: googleData.tokenId,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();
    setLoginData(data);
    localStorage.setItem("loginData", JSON.stringify(data));
  };
  return (
    <nav>
      <div className="menu-root">
        <img src={logo} alt="" className="" />
        <div>
          <ul>
            <li>
              <Link to={"/accueil"}>Accueil</Link>
            </li>
            <li>
              <Link to={"/repas"}>Profil</Link>
            </li>
            <li>
              <Link to={"/ficheRepas"}>Créer un repas</Link>
            </li>
          </ul>
        </div>
      </div>
      <div className="log-info">
        {loginData ? (
          <div>
            <span>{loginData.email}</span>
            <span className="deconnect-link">
              <a onClick={handleLogout} className="deconnect-link">
                Déconnexion
              </a>
            </span>
          </div>
        ) : (
          <GoogleLogin
            clientId={process.env.REACT_APP_CLIENT_ID}
            buttonText="Connexion"
            onSuccess={handleLogin}
            onFailure={handleFailure}
            cookiePolicy={"single_host_origin"}
            className="deconnect-link"
          ></GoogleLogin>
        )}

        {/* <span className="deconnect-link">
          <Link to={"/"}>Connexion</Link>
        </span> */}
      </div>
    </nav>
  );
}

export default NavBar;
