import GoogleLogin from "react-google-login";
import Logo from "../assets/img/logo-login.png";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { gapi } from "gapi-script";
import NavBar from "../components/NavBar";
import axios from "axios";
const ProfilPage = () => {
  gapi.load("client:auth2", () => {
    gapi.auth2.init({
      clientId: process.env.REACT_APP_CLIENT_ID,
      plugin_name: "chat",
    });
  });
  const [loginData, setLoginData] = useState(
    localStorage.getItem("loginData")
      ? JSON.parse(localStorage.getItem("loginData"))
      : null
  );

  let nomUser = loginData.name,
    mailuser = loginData.email;
  const [getRepas, setgetRepas] = useState([]);

  let paramID = useParams();

  let nomRepas, ing, rest, img;

  const fetchGetRepas = async () => {
    await axios
      .get(`http://localhost:5000/api/repas/${paramID.id}`)
      .then((response) => {
        setgetRepas(response.data);
        // console.log(response.data);
      });
  };

  useEffect(() => {
    fetchGetRepas();
  }, []);
  if (typeof paramID.id === "undefined") {
    nomRepas = "";
    ing = "";
    rest = "";
    img = "";
  } else {
    nomRepas = getRepas.nom;
    ing = getRepas.ingredient;
    rest = getRepas.restaurant;
    img = getRepas.image;
  }
  // ----------------------
  // update step
  // ----------------------

  const [nom, setName] = useState();
  const [ingredient, setIngredient] = useState();
  const [restaurant, setRestaurant] = useState();
  // const [image, setImage] = useState();
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log({ nom, ingredient, restaurant });

    await axios
      .put(`http://localhost:5000/api/repas/${paramID.id}`, {
        nom,
        ingredient,
        // image,
        restaurant,
      })
      .then((response) => {
        console.log(response.data);
        if (response.status === 200) {
          alert("Informations modifiées avec succès");
        }

        window.location.reload();
        // Handle response
      });
  };

  return (
    <>
      <NavBar />
      <div></div>
      <div className="login-section">
        <div className="login-logo-left">
          <img src={Logo} alt="Logo" />
        </div>
        <div className="login-content">
          <div>
            <h3>Profil</h3>
            {/* <button onClick={handleLogout} className="button-delete">
                  Se déconnecter
                </button> */}
            <form action="">
              <table>
                <tr>
                  <td>Nom</td>
                  <td>
                    <input
                      type="text"
                      name="nom"
                      id="nom"
                      defaultValue={nomUser}
                      //   onChange={(e) => setName(e.target.value)}
                      className="name-value"
                    />
                    {/* <h3>{getRepas.nom}</h3> */}
                  </td>
                </tr>
                <tr>
                  <td>Email</td>
                  <td>
                    <input
                      type="text"
                      name="ingredient"
                      id="ingredient"
                      defaultValue={mailuser}
                      //   onChange={(e) => setIngredient(e.target.value)}
                      className="name-value"
                    />
                  </td>
                </tr>
                <tr>
                  <td>Restaurant</td>
                  <td>
                    <input
                      type="text"
                      name="restaurant"
                      value={restuser}
                      className="name-value"
                      //   onChange={(e) => setRestaurant(e.target.value)}
                    />
                  </td>
                </tr>
                <tr>
                  <td>Secteur</td>
                  <td>
                    <input
                      type="text"
                      name="restaurant"
                      value={secteuruser}
                      className="name-value"
                      //   onChange={(e) => setRestaurant(e.target.value)}
                    />
                  </td>
                </tr>
              </table>

              <div className="btn-foot">
                {/* <div className="button-deconnexion" onClick={handleLogout}>
                  <span>
                    <a>Déconnexion</a>
                  </span>
                </div> */}
                <div className="button-deconnexion">
                  <span>
                    <Link to={"/repas"}>Enregistrer</Link>
                  </span>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProfilPage;
